import React, { useEffect, useState } from 'react'
import axios from 'axios';

const CheckOrderStatus = (props) => {
    console.log(props.previousStep.message)
    const [orderNumber, setOrderNumber] = useState(props.previousStep.message)
    const [message, setMessage] = useState(null)
    useEffect(() => {
        const checkOrder = async () =>{
            await axios.get(`http://localhost:8000/api/checkorderstatus/${orderNumber}`)
            .then(res=>{
                setMessage(res.data)
            }).catch(err =>{
                console.log(err)
            })
        }
        checkOrder();

        // eslint-disable-next-line
    }, [orderNumber])
    return (
        <div>
            {
                message === null || message === "" ?
                <p>Sorry your order number <span className="text-pink-600">{orderNumber}</span> is not found</p>
                :
                    <p>Your Order status : {message.status}</p>
            }
        </div>
    )
}

export default CheckOrderStatus
