import React, { useContext, useState } from "react";
import ProductsContext from "../../context/products/ProductsContext";

const AddCategory = () => {
  // destructure product context
  const { addCategory, errors } = useContext(ProductsContext);

  // declare state
  const [data, setData] = useState({
    categoryName: "",
    description: "",
  });
  // set error state
  const [error, setError] = useState(errors);

  // destructure data state
  const { categoryName, description } = data;

  // on input changes
  const onChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
    setError("");
  };

  //on submit of form
  const onSubmit = (e) => {
    e.preventDefault();

    addCategory(data);
  };
  return (
    <form onSubmit={onSubmit}>
      <div className="w-full block md:flex">
        <div className="w-full block">
          <label className="py-1 text-md text-gray-800">Name</label>
          <input
            type="text"
            name="categoryName"
            onChange={onChange}
            value={categoryName}
            required
            className="w-full outline-none"
          />
          {
            error?.categoryName && <label className="py-1 text-sm italic text-red-600">
            Error Message
          </label>
          }

        </div>
        <div className="w-full block">
          <label className="py-1 text-md text-gray-800">Name</label>
          <input
            type="text"
            name="description"
            onChange={onChange}
            value={description}
            required
            className="w-full outline-none"
          />
          {
            error?.categoryName && <label className="py-1 text-sm italic text-red-600">
            Error Message
          </label>
          }
        </div>
      </div>
      <div className="btn text-right">
        <button
          type="submit"
          className="mb-8 py-1 mt-2 px-4 border-1 bg-purple-600
           text-white font-semibold text-xl text-center outline-none capitalize"
        >
          Save Category
        </button>
      </div>
    </form>
  );
};

export default AddCategory;
