import React, { useContext, useState } from "react";
import AuthContext from "../context/auth/Context";

const Register = () => {
  const authContext = useContext(AuthContext);
  const { register, user } = authContext;
  // set state
  const [credentials, setCredentials] = useState({
    username: "",
    password: "",
  });
  //destructure state objects
  const { username, password } = credentials;
  //on change of inputs
  const onChange = (e) =>
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  // log in the user
  const registerUser = (credentials) => {
    if (username === "" || password === "") {
      alert("Fill in your credentials");
    }
    if (user.username === username) {
      alert("Username already taken");
    }
    //setLogin(credentials)
    register(credentials);
  };
  return (
    <div>
      <form onSubmit={registerUser} className="block rounded-md px-4 py-8 min-h-48">
        <input
          type="text"
          value={username}
          onChange={onChange}
          placeholder="Enter username / email"
          className="pl-2 py-2 mt-2 w-full rounded-full border-1"
        />
        <input
          type="password"
          value={password}
          onChange={onChange}
          className="pl-2 py-2 mt-2 w-full rounded-full border-1"
        />
        <input type="submit" value="Register" />
      </form>
    </div>
  );
};

export default Register;
