import React, { useContext, useEffect } from "react";
import ProductsContext from '../../context/products/ProductsContext';

import Item from "./Item";

const Shoes = (props) => {

  const productsContext = useContext(ProductsContext);
  /* const {getShoes, shoes } = productsContext; */

  const [shoes, setShoes] = useState([])

  useEffect(() => {
    const getHomePageCategoryProducts = async () => {
       await axios.get(
          `http://localhost:8000/api/products/homepage/shoes`
        ).then(res =>{
          setShoes(res.data)
        }).error(err =>{

        })
    };
    getHomePageCategoryProducts()
    // eslint-disable-next-line
  }, [])

  return (
    <div>
      <h2 className={`${window.location.href === 'http://localhost:3000/' && 'hidden'} text-center shadow-md mb-2 py-4 font-semibold text-xl font-serif w-full capitalize`}>{window.location.pathname.split('/')}</h2>

    <div className="grid grid-cols-1 sm:grid-cols-1 md:grid-cols-3 lg:grid-cols-3 xl:grid-cols-3 gap-5">

        {
            shoes !== null ? (
                shoes.length > 0 ? (
                    shoes.map((product, index)=> {
                        return(<Item product={product} key={index}/>)
                    }) 
                ) : "No data"
            ):'loading...'
        }
    </div>
    </div>
  );
};

export default Shoes;
