import React, { useContext, useEffect } from "react";
import ProductsContext from '../../context/products/ProductsContext';

import Item from "./Item";

const Jewellery = (props) => {

  const productsContext = useContext(ProductsContext);
  const {jewelleries, getJewelleries } = productsContext;

  useEffect(() => {
    getJewelleries()
    // eslint-disable-next-line
  }, [])

  return (
    <div>
      <h2 className={`${window.location.href === 'http://localhost:3000/' && 'hidden'} text-center py-2 font-semibold text-xl font-serif w-full capitalize`}>{window.location.pathname.split('%')}</h2>

    <div className="grid grid-cols-1 sm:grid-cols-1 md:grid-cols-3 lg:grid-cols-3 xl:grid-cols-3 gap-5">

        {
            jewelleries !== null ? (
                jewelleries.length > 0 ? (
                    jewelleries.map((product, index)=> {
                        return(<Item product={product} key={index}/>)
                    }) 
                ) : "No data"
            ):'loading...'
        }
    </div>
    </div>
  );
};

export default Jewellery;
