import React, { useEffect } from "react";
import { useReducer } from "react";
import AuthReducer from "./Reducer";
import AuthContext from "./Context";
import axios from "axios";

import {
  REGISTER,
  LOGIN,
  LOGOUT,
  UPDATE_PASSWORD,
  ADD_TO_ORDERS,
  ERRORS,
} from "../types";

const AuthState = (props) => {
  const initialState = {
    users: [],
    user: null,
    orders: [],
    errors: null,
    logedin: false,
  };

  const [state, dispatch] = useReducer(AuthReducer, initialState);

  const config = {
    headers: {
      "Content-Type": "application/json",
      Authorization: `Bearer ${localStorage.getItem("token")}`,
    },
  }

  if (localStorage.getItem("token")) {
    initialState.logedin = localStorage.getItem("token");
  } else {
    initialState.logedin = false;
  }
  // set local storage to logged in user
  /*if (window.localStorage.getItem('user')){
    initialState.login = true 
  }*/

  useEffect(() => {
    axios
      .get("http://localhost:8000/api/orders", config)
      .then((res) => {
        console.log(res);
      })
      .catch((err) => {
        console.log(err.response);
      });
    // eslint-disable-next-line
  }, [initialState.logedin]);

  //actions
  //Register user
  const register = async (user) => {
    await axios
      .post("http://localhost:8000/api/register", user, config)
      .then((res) => {
        dispatch({
          type: REGISTER,
          payload: res.data[0],
        });
      })
      .catch((err) => {
        var obj = err.response.data.errors ? err.response.data.errors : "";
        dispatch({
          type: ERRORS,
          payload: obj[Object.keys(obj)[0]],
        });
      });
  };

  //log user in
  const login = async (user) => {
    await axios
      .post("http://localhost:8000/api/login", user, config)
      .then((res) => {
        dispatch({
          type: LOGIN,
          payload: res.data[0],
        });
      })
      .catch((err) => {
        var obj = err.response.data.errors ? err.response.data.errors : "";
        dispatch({
          type: ERRORS,
          payload: obj[Object.keys(obj)[0]],
        });
      });
  };

  //log user out
  const setError = (err) => {
    dispatch({
      type: ERRORS,
      payload: err,
    });
  };

  //log user out
  const logout = () => {
    dispatch({
      type: LOGOUT,
    });
  };

  //log user out
  const updatePassword = (credentials) => {
    dispatch({
      type: UPDATE_PASSWORD,
      payload: credentials,
    });
  };
  //add to order
  const addToOrders = (product) => {
    dispatch({
      type: ADD_TO_ORDERS,
      payload: product,
    });
  };

  return (
    <AuthContext.Provider
      value={{
        user: state.user,
        users: state.users,
        logedin: state.logedin,
        orders: state.orders,
        errors: state.errors,
        login,
        register,
        logout,
        updatePassword,
        addToOrders,
        setError,
      }}
    >
      {props.children}
    </AuthContext.Provider>
  );
};

export default AuthState;
