import React, { useContext, useEffect, useState } from "react";
import { Bar } from "react-chartjs-2";

import Category from "./Category";
import ProductsContext from "../../context/products/ProductsContext";
import PieChart from "../charts/PieChart";

const ListCategories = () => {
  // destructure values from context
  const { categories, getCategories, success } = useContext(ProductsContext);

  const [catNames] = useState([]);
  const [catQtys] = useState([]);

  useEffect(() => {
    const fetchCats = async () => {
      await getCategories();
      if (categories?.length > 0) {
        for (const dataObj of categories) {
          catNames.push(dataObj.name);
          catQtys.push(dataObj.products.length);
        }
      }
    };
    fetchCats();

    // eslint-disable-next-line
  }, [categories?.length > 0]);

  return (
    <div className="m-auto md:w-2/3 px-4 block">
      <div>
        <Bar
          height={200}
          width={300}
          data={{
            labels: catNames?.length > 0 && catNames,
            datasets: [
              {
                label: " Categories",
                data: catQtys?.length > 0 && catQtys,
              },
            ],
          }}
          options={{
            maintainAspectRation: false,
          }}
        />
      </div>
      <table className="w-full text-sm lg:text-base" cellSpacing="0">
        <thead>
          <tr className="h-12 uppercase">
            <th className="text-left">Name</th>
            <th className="text-center md:table-cell">Qty</th>
            <th className="text-right md:table-cell"></th>
          </tr>
        </thead>
        <tbody>
          {categories?.length > 0 ? (
            categories.map((category, index) => {
              return <Category category={category} key={index} />;
            })
          ) : (
            <tr className="text-center">
              <td>No categories yet</td>
            </tr>
          )}
        </tbody>
      </table>
      {success && (
        <div className="py-4 text-blue-800 text-md italic text-center">
          {success.message}
        </div>
      )}
    </div>
  );
};

export default ListCategories;
