import React from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

const Sidebar = () => {
  return (
    <div className="w-full text-center md:w-48 md:h-screen md:h-full md:fixed md:bg-gray-100">
      <Router>
        <nav>
          <ul className="flex md:block justify-center md:text-left">
            <li className="hover:bg-purple-400 px-4 py-2 md:px-0 md:py-0">
              <Link to="/shoes">Shoes</Link>
            </li>
            <li className="hover:bg-purple-400 px-4 py-2 md:px-0 md:py-0">
              <Link to="/technology">Technology</Link>
            </li>
            <li className="hover:bg-purple-400 px-4 py-2 md:px-0 md:py-0">
              <Link to="/shoes">Shirts</Link>
            </li>
          </ul>
        </nav>
      </Router>
    </div>
  );
};

export default Sidebar;
