import { useReducer } from "react";
import axios from "axios";
import ProductsReducer from "./ProductsReducer";
import ProductsContext from "./ProductsContext";

import {
  ADD_PRODUCT,
  UPDATE_PRODUCT,
  DELETE_PRODUCT,
  GET_PRODUCTS,
  GET_PRODUCT,
  GET_CATEGORY,
  ADD_CATEGORY,
  UPDATE_CATEGORY,
  DELETE_CATEGORY,
  GET_CATEGORIES,
  GET_ELECTRONICS,
  GET_SHOES,
  GET_CLOTHINGS,
  SEARCH_PRODUCTS,
  GET_CATEGORY_PRODUCTS,
  GET_RELATED_PRODUCTS,
  ERRORS,
  CLEAR_MESSAGES,
} from "../types";

const ProductsState = (props) => {
  const initialState = {
    products: null,
    product: null,
    categoryProducts: null,
    categories: null,
    category:null,
    shoes: null,
    clothings: null,
    electronics: null,
    relatedProducts: null,
    searchedItems: null,
    loading: true,
    errors: null,
    success: null,
  };

  //initialise the state
  const [state, dispatch] = useReducer(ProductsReducer, initialState);

  //configuration
  const config = {
    headers: {
      "Content-Type": "application/json",
      Authorization: `Bearer ${localStorage.getItem("token")}`,
    },
  };

  //set the actions
  //**add product to db */
  const addProduct = async (data) => {

    try {
      await axios
        .post("http://localhost:8000/api/product", data, config)
        .then((res) => {
          dispatch({
            type: ADD_PRODUCT,
            payload: res.data,
          });
        })
        .catch((err) => {
          var obj = err.response.data.errors ? err.response.data.errors : "";
          dispatch({
            type: ERRORS,
            /* payload: obj[Object.keys(obj)[0]], */
            payload: obj,
          });
        });
    } catch (error) {}
  };

  // add category
  const addCategory = async (data) => {
    try {
      await axios
        .post("http://localhost:8000/api/category", data, config)
        .then((res) => {
          dispatch({
            type: ADD_CATEGORY,
            payload: res.data,
          });
        })
        .catch((err) => {
          var obj = err.response.data.errors ? err.response.data.errors : "";
          dispatch({
            type: ERRORS,
            /* payload: obj[Object.keys(obj)[0]], */
            payload: obj,
          });
        });
    } catch (error) {}
  };

  //**update product to db */
  const updateProduct = async (id, data) => {
    try {
      await axios
        .post(`http://localhost:8000/api/product/${id}`, data, config)
        .then((res) => {
          dispatch({
            type: UPDATE_PRODUCT,
            payload: res.data,
          });
        })
        .catch((err) => {
          var obj = err.response.data.errors ? err.response.data.errors : "";
          dispatch({
            type: ERRORS,
            payload: obj[Object.keys(obj)[0]],
          });
        });
    } catch (error) {}
  };

  //**update category to db */
  const updateCategory = async (id, data) => {
    try {
      await axios
        .post(`http://localhost:8000/api/category/${id}`, data, config)
        .then((res) => {
          dispatch({
            type: UPDATE_CATEGORY,
            payload: res.data,
          });
        })
        .catch((err) => {
          var obj = err.response.data.errors ? err.response.data.errors : "";
          dispatch({
            type: ERRORS,
            payload: obj[Object.keys(obj)[0]],
          });
        });
    } catch (error) {}
  };

  //**delete product from db */
  const deleteProduct = async (id) => {
    try {
      await axios
        .delete(`http://localhost:8000/api/product/${id}`, config)
        .then((res) => {
          dispatch({
            type: DELETE_PRODUCT,
          });
        })
        .catch((err) => {
          var obj = err.response.data.errors ? err.response.data.errors : "";
          dispatch({
            type: ERRORS,
            payload: obj[Object.keys(obj)[0]],
          });
        });
    } catch (error) {}
  };


  //**delete category from db */
  const deleteCategory = async (id) => {
    try {
      await axios
        .delete(`http://localhost:8000/api/category/${id}`, config)
        .then((res) => {
          dispatch({
            type: DELETE_CATEGORY,
            payload: res.data
          });
        })
        .catch((err) => {
          var obj = err.response.data.errors ? err.response.data.errors : "";
          dispatch({
            type: ERRORS,
            payload: obj[Object.keys(obj)[0]],
          });
        });
    } catch (error) {}
  };

  // get products
  const getProducts = async () => {
    try {
      const res = await axios.get("http://localhost:8000/api/products", config);
      dispatch({
        type: GET_PRODUCTS,
        payload: res.data,
      });
    } catch (error) {}
  };

  // get categories
  const getCategories = async () => {
    try {
      await axios
        .get("http://localhost:8000/api/categories", config)
        .then((res) => {
          dispatch({
            type: GET_CATEGORIES,
            payload: res.data,
          });
        })
        .error((err) => {
          console.log(err);
        });
    } catch (error) {}
  };

  //**get specific products products */
  //get products based on categories
  const getCategoryProducts = async (category) => {
    await axios.get(
        `http://localhost:8000/api/products/category/${category}`
      ).then(res =>{
        dispatch({
        type: GET_CATEGORY_PRODUCTS,
        payload: res.data[0].products,
      })
      }).catch(err =>{
        dispatch({
          type: ERRORS,
          payload: "No product for this category",
        });
      })
  };
  /* //get Jewelery
  const getHomePageCategoryProducts = async (category) => {
    try {
      const res = await axios.get(
        `http://localhost:8000/api/products/homepage/{catName}`
      );
      dispatch({
        type: GET_SHOES,
        payload: res.data,
      });
    } catch (error) {}
  }; */

  //get Jewelery
  const getShoes = async () => {
    try {
      const res = await axios.get(
        `http://localhost:8000/api/category/products/1`
      );
      dispatch({
        type: GET_SHOES,
        payload: res.data,
      });
    } catch (error) {}
  };

  //get Womens' Clothing
  const getClothings = async () => {
    try {
      const res = await axios.get(
        "https://fakestoreapi.com/products/category/women's clothing"
      );
      dispatch({
        type: GET_CLOTHINGS,
        payload: res.data,
      });
    } catch (error) {}
  };

  //get Electronics
  const getElectronics = async () => {
    try {
      const res = await axios.get(
        "https://fakestoreapi.com/products/category/electronics?limit=6"
      );
      dispatch({
        type: GET_ELECTRONICS,
        payload: res.data,
      });
    } catch (error) {}
  };

  //get related products
  const getRelatedProducts = async (categoryId) => {
    try {
      const res = await axios.get(
        `http://localhost:8000/api/related/products/${categoryId}?limit=6`,
        config
      );

      dispatch({
        type: GET_RELATED_PRODUCTS,
        payload: res.data,
      });
    } catch (error) {}
  };

  //**get a product */
  const getProduct = async (id) => {
    try {
      const res = await axios.get(`http://localhost:8000/api/product/${id}`);
      dispatch({
        type: GET_PRODUCT,
        payload: res.data,
      });
    } catch (error) {}
  };


  //**get a category */
  const getCategory = async (id) => {
    try {
      const res = await axios.get(`http://localhost:8000/api/category/${id}`);
      dispatch({
        type: GET_CATEGORY,
        payload: res.data,
      });
    } catch (error) {}
  };

  // search product
  const searchProducts = async (text) => {
    await axios.axios.get(`http://localhost:8000/api/search?text=${text}`)
    .then(res =>{
      dispatch({
        type: SEARCH_PRODUCTS,
        payload: res.data,
      });
    }).catch(err =>{
      dispatch({
        type: ERRORS,
        payload: "Your search returns 0 results",
      });
    })

  };

  // set product to null
  const setProductToNull = () => {
    dispatch({
      type: GET_PRODUCT,
      payload: null,
    });
  };

  // set category to null
  const setCategoryToNull = () => {
    dispatch({
      type: GET_CATEGORY,
      payload: null,
    });
  };

  //Clear messages if any
  const clearMessages = () => {
    dispatch({
      type: CLEAR_MESSAGES,
    });
  };




  return (
    <ProductsContext.Provider
      value={{
        products: state.products,
        shoes: state.shoes,
        clothings: state.clothings,
        electronics: state.electronics,
        relatedProducts: state.relatedProducts,
        product: state.product,
        category: state.category,
        searchedItems: state.searchedItems,
        loading: state.loading,
        error: state.error,
        success: state.success,
        categories: state.categories,
        errors: state.errors,
        addProduct,
        updateProduct,
        deleteProduct,
        addCategory,
        updateCategory,
        deleteCategory,
        getProducts,
        getCategories,
        getShoes,
        getClothings,
        getElectronics,
        getProduct,
        getCategory,
        searchProducts,
        getCategoryProducts,
        getRelatedProducts,
        setProductToNull,
        setCategoryToNull,
        clearMessages,
      }}
    >
      {props.children}
    </ProductsContext.Provider>
  );
};

export default ProductsState;
