import React, { useContext, useState } from "react";
import { Link } from "react-router-dom";
import AuthContext from "../context/auth/Context";

const LoginPage = () => {
  const authContext = useContext(AuthContext);
  const { login, user } = authContext;
  // set state
  const [credentials, setCredentials] = useState({
    username: "",
    password: "",
  });
  //destructure state objects
  const { username, password } = credentials;
  //on change of inputs
  const onChange = (e) =>
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  // log in the user
  const loginUser = (e) => {
    e.preventDefault();
    if (username === "" || password === "") {
      alert("Fill in your credentials");
    }
    if (user.username !== username) {
      alert("Invalid credentials");
    }
    //setLogin(credentials)
    login(credentials);
  };
  return(
  <div className="p-1 block bg-gray-500 rounded-md min-h-screen">
      <div className="w-full text-center mt-8 font-bold text-2xl text-white">
        LOGIN
      </div>
      <form onSubmit={loginUser} className="block px-4 py-8">
        <input
          type="text"
          name="username"
          value={username}
          onChange={onChange}
          placeholder="Enter username / email"
          className="pl-2 py-2 mt-2 w-full rounded-full border-1"
        />
        <input
          type="password"
          name="password"
          value={password}
          onChange={onChange}
          className="pl-2 py-2 mt-2 w-full rounded-full border-1"
        />
        <button
          type="submit"
          className="pl-2 py-2 mt-4 w-full rounded-full border-1 bg-purple-600 text-white font-bold text-3xl"
        >
          Submit
        </button>
      </form>
      <p className="text-center">
        A new member?, please{" "}
        <Link to="/register" className="text-white-800 px-1">
          register
        </Link>{" "}
      </p>
    </div>
  );
};

export default LoginPage;
