import React from "react";
import img from "../../../images/shirt.jpg";
const Item = () => {
  return (
    <div className="block p-2 rounded-t lg:rounded-t-none lg:rounded-l border-r border-b border-l border-gray-400 lg:border-l-0 lg:border-t lg:border-gray-400 bg-white ">
      <div className="w-full lg:max-w-full lg:flex">
        <div
          className="h-40 lg:w-full flex-none bg-cover rounded-l text-center overflow-hidden"
          style={{ backgroundImage: `url(${img})` }}
          title="Mountain"
        ></div>
        <div className="leading-normal z-10">
          <div className="mb-8">
            <div className="text-gray-600 font-bold transition duration-500 hover:text-gray-900 -ml-16
            mb-2">
              view Details
            </div>
          </div>
        </div>
      </div>
      <div className="px-6 pt-2 pb-2">
        <span className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2 mb-2">
          Price
        </span>
        <span className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-pink-500 mr-2 mb-2">
          Add to Cart
        </span>
      </div>
    </div>
  );
};

export default Item;
