import React, { useContext, useEffect } from "react";
import ProductsContext from '../context/products/ProductsContext';
import Item from './Item';


const CategoryProducts = (props) => {

  const productsContext = useContext(ProductsContext);
  const { getCategoryProducts, products, errors } = productsContext;

  useEffect(() => {
    getCategoryProducts(props.match.params.cat)

    // eslint-disable-next-line
  }, [props.match.params.cat])

  return (
    <div>
      <h2 className={`${window.location.href === 'http://localhost:3000/' && 'hidden'}
       text-center shadow-md mb-2 py-4 font-semibold text-xl font-serif 
       w-full capitalize`}>{props.match.params.cat}</h2>

    <div className="grid grid-cols-1 sm:grid-cols-1 md:grid-cols-3 lg:grid-cols-3 xl:grid-cols-3 gap-5">

        {
            products !== null ? (
                products.length > 0 ? (
                    products.map((product, index)=> {
                        return(<Item product={product} key={index}/>)
                    }) 
                ) : "No data"
            ):'Loading...'
        }
    </div>
    </div>
  );
};

export default CategoryProducts;
