import React, { useContext, useState } from "react";
import AuthContext from "../context/auth/Context";

const Register = () => {
  const authContext = useContext(AuthContext);
  const { register, user } = authContext;
  // set state
  const [credentials, setCredentials] = useState({
    username: "",
    password: "",
  });
  //destructure state objects
  const { username, password } = credentials;
  //on change of inputs
  const onChange = (e) =>
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  // register new user
  const registerUser = (e) => {
    e.preventDefault();
    if (username === "" || password === "") {
      alert("Fill in your credentials");
    }
    if (user.username === username) {
      alert("Username already taken");
    }
    //setLogin(credentials)
    register(credentials);
  };
  return (
    <div className="py-2 bg-gray-400 rounded-md min-h-screen">
      <h2 className="text-center mt-8 bg-purple-300 mb-2 font-bold text-2xl">Register</h2>
      <form onSubmit={registerUser} className="block px-4 py-8">
        <input
          type="text"
          name="username"
          value={username}
          onChange={onChange}
          placeholder="Enter username / email"
          className="pl-2 py-2 mt-2 w-full rounded-full border-1"
        />
        <input
          type="password"
          name="password"
          value={password}
          onChange={onChange}
          className="pl-2 py-2 mt-2 w-full rounded-full border-1"
        />
        <button
          type="submit"
          className="pl-2 py-2 mt-4 w-full rounded-full border-1 bg-purple-600 text-white font-bold"
        >
          Register
        </button>
      </form>
    </div>
  );
};

export default Register;
