import React, { Fragment, useState } from "react";

const Order = (props) => {
  const { item } = props;
  const [openOrder, setOpenOrder] = useState(false);

  return (
    /* : item.map((transaction, index) => { */
    <Fragment>
      {item === null ? (
        "Loading"
      ) : (
        <div
          className={`w-full block transition duration-500 py-1 px-1 shadow-md mb-2`}
        >
          <div
            className={`${openOrder && 'p-2 shadow-md'} flex justify-between cursor-pointer`}
            onClick={() => setOpenOrder(!openOrder)}
          >
            <img
              src={item.image}
              alt={item.price}
              className="flex-shrink-0 w-8 h-8 rounded-full mr-2"
            />
            <div className="flex-grow text-center text-green-600">
              <span className="text-gray-600">order id:</span> {item.transaction_id}
            </div>
            <i
              className={`text-center px-2 py-1 cursor-pointer text-xl text-purple-600 font-semibold fa fa-angle-${
                openOrder ? "down" : "up"
              }`}
            ></i>
          </div>
          <div
            className={`${
              openOrder ? "block" : "hidden"
            } transition duration-500`}
          >
            {item.orders.length > 0 &&
              item.orders.map((i, index) => {
                return (
                  <div key={index} className="">
                    {i.products.length > 0 &&
                      i.products.map((p, index) => {
                        return (
                          <div key={index}>
                            <div className="p-2 block">
                              <div className="pl-2 font-semibold">{p.title}</div>
                            </div>
                            <div className="ml-4 block">
                              <div className="text-sm pl-1 bg-gray-300 py-1">
                                Price
                              </div>
                              <div className="p-1">{p.price}</div>
                            </div>
                            <div className="ml-4 block">
                              <div className="text-sm pl-1 bg-gray-300 py-1">
                                Quantity
                              </div>
                              <div className="p-1">{p.qty}</div>
                            </div>
                            <div className="ml-4 mb-2 block">
                              <div className="text-sm pl-1 bg-gray-300 py-1">
                                Description
                              </div>
                              <div className="p-1">{p.description}</div>
                            </div>
                          </div>
                        );
                      })}
                  </div>
                );
              })}
          </div>
        </div>
      )}
    </Fragment>
  );
};

export default Order;
