import React, { useContext, useState, useEffect } from "react";
import ProductsContext from "../../context/products/ProductsContext";

const AddProduct = () => {
  const { errors, addProduct, categories, success, getCategories } =
    useContext(ProductsContext);

  //set error into state
  const [error, setError] = useState(errors);

  //set data state
  const [data, setData] = useState({
    title: "",
    price: "",
    deductions: "",
    description: "",
    qty: "",
    category: "",
  });

  const [ImageName, setImageName] = useState('')

  //destructure data state
  const { title, price, deductions, description, qty, category } =
    data;

  //component
  useEffect(() => {
    getCategories();
    // eslint-disable-next-line
  }, []);

  //on input change
  const onChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
    setImageName(e.target.files[0].name);
  };


  // on submit
  const onSubmit = (e) => {
    e.preventDefault();
    console.log(e.target.input)
    let formData = new FormData();
    formData.append('title', title);
    formData.append('price', price);
    formData.append('qty', qty);
    formData.append('deductions', deductions);
    formData.append('image_name', ImageName);
    formData.append('category', category);
    formData.append('description', description);
    addProduct(formData);
  };

  return (
    <form onSubmit={onSubmit} encType="multipart/form-data" className="block  md:px-4 px-8 md:ml-48 pb:8">
      <div className="w-full grid grid-cols-1 grid md:grid-cols-2 lg:grid-cols-3 gap-4">
        <div className="block">
          <label htmlFor="Title" className="text-md text-gray-800 py-1">
            Title
          </label>
          <input
            type="text"
            name="title"
            onChange={onChange}
            value={title}
            required
            className={`${
              errors?.title && "border-red-600 "
            } border border-gray-400 w-full py-1 px-1 outline-none focus:border-purple-300`}
          />
          {errors?.title && (
            <label className="text-sm italic text-red-600 py-1">
              {errors.title}
            </label>
          )}
        </div>
        <div className="block">
          <label className="text-md text-gray-800 py-1">Price</label>
          <input
            type="text"
            name="price"
            onChange={onChange}
            value={price}
            required
            className={`${
              errors?.price && "border-red-600 "
            } border border-gray-400 w-full py-1 px-1 outline-none focus:border-purple-300`}
          />
          {errors?.price && (
            <label className="text-sm italic text-red-600 py-1">
              {errors.price}
            </label>
          )}
        </div>
        <div className="block">
          <label className="text-md text-gray-800">Deductions</label>
          <input
            type="text"
            name="deductions"
            onChange={onChange}
            value={deductions}
            required
            className={`${
              errors?.deductions && "border-red-600 "
            } border border-gray-400 w-full py-1 px-1 outline-none focus:border-purple-300`}
          />
          {errors?.deductions && (
            <label className="text-sm italic text-red-600 py-1">
              {errors.deductions}
            </label>
          )}
        </div>
        <div className="block">
          <label className="text-md text-gray-800">Description</label>
          <textarea
            type="text"
            name="description"
            onChange={onChange}
            value={description}
            required
            className={`${
              errors?.description && "border-red-600 "
            } border border-gray-400 w-full py-1 px-1 outline-none focus:border-purple-300`}
          ></textarea>
          {errors?.description && (
            <label className="text-sm italic text-red-600 py-1">
              {errors.description}
            </label>
          )}
        </div>
        <div className="block">
          <label className="text-md text-gray-800">Qty</label>
          <input
            type="number"
            name="qty"
            onChange={onChange}
            value={qty}
            required
            className={`${
              errors?.qty && "border-red-600 "
            } border border-gray-400 w-full py-1 px-1 outline-none focus:border-purple-300`}
          />
          {errors?.qty && (
            <label className="text-sm italic text-red-600 py-1">
              {errors.qty}
            </label>
          )}
        </div>
        <div className="block">
          <label className="text-md text-gray-800">Select Category</label>
          <select
            name="category"
            className="border border-gray-400 w-full py-1 px-1 outline-none focus:border-purple-300"
          >
            {categories?.length > 0 ? (
              categories.map((category, idx) => {
                return (
                  <option value={category.id} index={idx}>
                    {category.name}
                  </option>
                );
              })
            ) : (
              <option value="None">No category</option>
            )}
          </select>
        </div>
        <div className="block">
          <label className="text-md text-gray-800">Choose Image</label>
          <input
            type="file"
            name="ImageName"
            onChange={onChange}
            value={ImageName}
            required
            className={`${
              errors?.title && "border-red-600 "
            } border border-gray-400 w-full py-1 px-1 outline-none focus:border-purple-300`}
          />
          {errors?.image_name && (
            <label className="text-sm italic text-red-600 py-1">
              {errors.image_name}
            </label>
          )}
        </div>
      </div>
      {success && (
        <div className="py-4 text-blue-800 text-md italic text-center">
          {success.message}
        </div>
      )}
      <div className="btn text-right">
        <button
          type="submit"
          className="mb-8 py-1 mt-2 px-4 border-1 bg-purple-600
           text-white font-semibold text-xl text-center outline-none capitalize"
        >
          save product
        </button>
      </div>
    </form>
  );
};

export default AddProduct;
