import React, { Fragment , useState } from "react";

const Order = (props) => {
  const { item } = props;
  const [openOrder, setOpenOrder] = useState(false);

  return (
    <Fragment>
      {
  item === null
    ? "Loading"
    : item.map((transaction) => {
        return <div
          className={`w-full block transition duration-500 py-3 px-1 shadow-md mb-2`}
        >
          <div
            className="flex justify-between cursor-pointer"
            onClick={() => setOpenOrder(!openOrder)}
          >
            <img
              src={item.image}
              alt={item.price}
              className="flex-shrink-0 w-8 h-8 rounded-full mr-2"
            />
            <div className="flex-grow text-left bg-red-600">
              {transaction.transaction_id}
              {console.log(transaction.transaction_id)}
            </div>
            <i
              className={`text-center px-2 py-1 cursor-pointer text-xl text-purple-600 font-semibold fa fa-angle-${
                openOrder ? "down" : "up"
              }`}
            ></i>
          </div>
          <div
            className={`${
              openOrder ? "hidden" : "block"
            } transition duration-500`}
          >
            {
         transaction.orders.map((i, index) => { 
          <div key={index}>
          <div className="p-4 block">
              <div className="pl-2">Product Name</div>
            </div>
            Lorem ipsum dolor sit amet.
            <div className="p-4 block">
              <div className="text-sm pl-1 bg-gray-300 py-1">Price</div>
              <div className="pl-2">{i.id}</div>
            </div>
            <div className="p-4 block">
              <div className="text-sm pl-1 bg-gray-300 py-1">Quantity</div>
              <div className="pl-2">{i.qty}</div>
            </div>
            <div className="p-4 block">
              <div className="text-sm pl-1 bg-gray-300 py-1">Description</div>
              <div className="pl-2">{i.description}</div>
            </div>
          </div>;
          })} 
          </div>
        </div>
      })
    }
        </Fragment>
  )
};

export default Order;
