import React, { useContext, useRef } from "react";
import ProductsContext from './context/products/ProductsContext';


const Search = () => {

  const productsContext = useContext(ProductsContext);
  const {searchProduct } = productsContext;

  const text = useRef('')

    const searchItem = (e) =>{
        e.preventDefault()
        searchProduct(text.current.value)
    }
    return (
        <form onSubmit={searchItem}>
          <input value={text} type="text" placeholder="Search" className="pl-4 mt-4 mb-8 py-2 w-full ml-1 mr-2 rounded-full border-1"/>
        </form>
    )
}

export default Search
