import { useReducer } from "react";
import AuthReducer from "./Reducer";
import AuthContext from "./Context";
import axios from "axios";

import {
  REGISTER,
  LOGIN,
  LOGOUT,
  UPDATE_PASSWORD,
  ADD_TO_ORDERS,
} from "../types";

const AuthState = (props) => {
  const initialState = {
    users: [],
    user: null,
    orders: [],
    errors: null,
    logedin: false,
  };

  const [state, dispatch] = useReducer(AuthReducer, initialState);

  // set local storage to logged in user
  /*if (window.localStorage.getItem('user')){
    initialState.login = true 
  }*/

  //actions
  //Register user
  const register = async (user) => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.post(
        "http://localhost:8000/api/register",
        user,
        config
      );
      dispatch({
        type: REGISTER,
        payload: res.data,
      });
    } catch (err) {}
  };

  //log user in
  const login = (user) => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = axios.post("http://localhost:8000/api/login", user, config);

      dispatch({
        type: LOGIN,
        payload: res.data,
      });
    } catch (err) {
      console.log(err);
    }
  };

  //log user out
  const logout = () => {
    dispatch({
      type: LOGOUT,
    });
  };

  //log user out
  const updatePassword = (credentials) => {
    dispatch({
      type: UPDATE_PASSWORD,
      payload: credentials,
    });
  };
  //add to order
  const addToOrders = (product) => {
    dispatch({
      type: ADD_TO_ORDERS,
      payload: product,
    });
  };

  return (
    <AuthContext.Provider
      value={{
        user: state.user,
        users: state.users,
        logedin: state.logedin,
        orders: state.orders,
        login,
        register,
        logout,
        updatePassword,
        addToOrders,
      }}
    >
      {props.children}
    </AuthContext.Provider>
  );
};

export default AuthState;
