import React, { useContext, useEffect, useState } from "react";
import { withRouter } from "react-router";
import ProductsContext from "./context/products/ProductsContext";
import ReactPaginate from "react-paginate";
import Item from "./Item";
const SearchedItem = (props) => {
  const productsContext = useContext(ProductsContext);
  const {setSearchchedItemToNull, searchProducts, searchedItems } = productsContext;
  const [offset, setOffset] = useState(0);
  const [perPage] = useState(3);


  useEffect(() => {
    const fetchproducts = async () =>{
      await searchProducts(props.match.params.txt);
    }
    fetchproducts()

    return () =>{
      setSearchchedItemToNull()
    }
    // eslint-disable-next-line
  }, []);

  const slice =
    searchedItems !== null && searchedItems.slice(offset, offset + perPage);


  const handlePageClick = (e) => {
    const selectedPage = e.selected;
    setOffset(selectedPage * perPage);
  };
  return (
    <div className="block mb-24">
      <div className="grid grid-cols-1 sm:grid-cols-1 md:grid-cols-3 lg:grid-cols-3 xl:grid-cols-3 gap-5">
        {searchedItems !== null 
        ? searchedItems.length > 0
          ? slice.map((product, index) => (
              <Item product={product} key={index} />
            ))
          : "No data"
          : "Loading"
        }
      </div>
      {searchedItems !== null && searchedItems.length > 0 && (
        <ReactPaginate
          previousLabel={"Prev"}
          nextLabel={"Next"}
          breakLabel={"..."}
          breakClassName={"break-me"}
          pageCount={Math.ceil(
            searchedItems !== null && searchedItems.length / perPage
          )}
          marginPagesDisplayed={2}
          pageRangeDisplayed={5}
          onPageChange={handlePageClick}
          containerClassName={
            "pagination w-full bg-purple-500 flex justify-center"
          }
          subContainerClassName={"pages pagination"}
          activeClassName={"active"}
          pageClassName={"px-2"}
        />
      )}
    </div>
  );
};

export default withRouter(SearchedItem);
