import {
  ADD_TO_CART,
  REMOVE_FROM_CART,
  INCREASE_CART_ITEM_QTY,
  DECREASE_CART_ITEM_QTY,
} from "../types";

const CartReducer = (state, action) => {
  switch (action.types) {
    case ADD_TO_CART:
      return {
        ...state,
        cart: action.payload,
      };
    case REMOVE_FROM_CART:
      return {
        ...state,
        cart: action.payload,
      };
    case INCREASE_CART_ITEM_QTY:
      return {
        ...state,
        cart: action.payload,
      };
    case DECREASE_CART_ITEM_QTY:
      return {
        ...state,
        cart: action.payload,
      };

    default:
      return {
        ...state,
      };
  }
};

export default CartReducer;
