import React, { useContext, useState } from "react";
import ProductsContext from "../../context/products/ProductsContext";

const AddProduct = () => {
  const { errors } = useContext(ProductsContext);

  //set error into state
  const [error, setError] = useState(errors)

  //set data state
  const [data, setData] = useState({
    title: "",
    price: "",
    deductions: "",
    description: "",
    qty: "",
    category: "",
    image_name: "",
  });

  //destructure data state
  const { title, price, deductions, description, qty, category, image_name } =
    data;

  //on input change
  const onChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
  };

  // on submit 
  const onSubmit = (e) =>{
    e.preventDefault();
  }

  return (
    <form onSubmit={onSubmit} className="block p-1">
      <div className="w-full grid grid-cols-1 grid md:grid-cols-3 gap-4">
        <div className="block">
          <label htmlFor="Title" className="text-md text-gray-800 py-1">
            Title
          </label>
          <input
            type="text"
            name="title"
            onChange={onChange}
            value={title}
            required
            className={`${
              error?.title && "border-red-600 "
            } border border-gray-400 w-full py-1 px-1 outline-none focus:border-purple-300`}
          />
          <label className="text-sm italic text-red-600 py-1">
            Error message
          </label>
        </div>
        <div className="block">
          <label className="text-md text-gray-800 py-1">Price</label>
          <input
            type="text"
            name="price"
            onChange={onChange}
            value={price}
            required
            className={`${
              error?.price && "border-red-600 "
            } border border-gray-400 w-full py-1 px-1 outline-none focus:border-purple-300`}
          />
          <label className="text-sm italic text-red-600 py-1">
            Error message
          </label>
        </div>
        <div className="block">
          <label className="text-md text-gray-800">Deductions</label>
          <input
            type="text"
            name="deductions"
            onChange={onChange}
            value={deductions}
            required
            className={`${
              error?.deductions && "border-red-600 "
            } border border-gray-400 w-full py-1 px-1 outline-none focus:border-purple-300`}
          />
          <label className="text-sm italic text-red-600 py-1">
            Error message
          </label>
        </div>
        <div className="block">
          <label className="text-md text-gray-800">Description</label>
          <input
            type="text"
            name="description"
            onChange={onChange}
            value={description}
            required
            className={`${
              error?.description && "border-red-600 "
            } border border-gray-400 w-full py-1 px-1 outline-none focus:border-purple-300`}
          />
          <label className="text-sm italic text-red-600 py-1">
            Error message
          </label>
        </div>
        <div className="block">
          <label className="text-md text-gray-800">Qty</label>
          <input
            type="number"
            name="qty"
            value={qty}
            required
            className={`${
              error?.qty && "border-red-600 "
            } border border-gray-400 w-full py-1 px-1 outline-none focus:border-purple-300`}
          />
          <label className="text-sm italic text-red-600 py-1">
            Error message
          </label>
        </div>
        <div className="block">
          <label className="text-md text-gray-800">Select Category</label>
          <input
            type="text"
            name="category"
            onChange={onChange}
            value={category}
            required
            className={`${
              error?.title && "border-red-600 "
            } border border-gray-400 w-full py-1 px-1 outline-none focus:border-purple-300`}
          />
          <label className="text-sm italic text-red-600 py-1">
            Error message
          </label>
        </div>
        <div className="block">
          <label className="text-md text-gray-800">Choose Image</label>
          <input
            type="file"
            name="image_name"
            onChange={onChange}
            value={image_name}
            required
            className={`${
              error?.title && "border-red-600 "
            } border border-gray-400 w-full py-1 px-1 outline-none focus:border-purple-300`}
          />
          <label className="text-sm italic text-red-600 py-1">
            Error message
          </label>
        </div>
      </div>
      <button className="rounded-md py-2 px-4 bg-blue-700 text-white font-semibold text-2xl">
        Save Product
      </button>
    </form>
  );
};

export default AddProduct;
