import { useReducer } from "react";
import axios from "axios";
import ProductsReducer from "./ProductsReducer";
import ProductsContext from "./ProductsContext";

import {
  ADD_PRODUCT,
  UPDATE_PRODUCT,
  DELETE_PRODUCT,
  GET_PRODUCTS,
  GET_PRODUCT,
  ADD_CATEGORY,
  UPDATE_CATEGORY,
  DELETE_CATEGORY,
  GET_CATEGORIES,
  GET_ELECTRONICS,
  GET_JEWELLERIES,
  GET_WOMENS_CLOTHING,
  GET_MENS_CLOTHING,
  SEARCH_PRODUCTS,
  GET_CATEGORY_PRODUCTS,
  GET_RELATED_PRODUCTS,
  ERRORS,
  CLEAR_MESSAGES,
} from "../types";

const ProductsState = (props) => {
  const initialState = {
    products: null,
    categoryProducts: null,
    categories: null,
    jewelleries: null,
    mensClothing: null,
    womensClothing: null,
    electronics: null,
    relatedProducts: null,
    product: null,
    searchedItems: null,
    loading: true,
    errors: null,
    success: null,
  };

  //initialise the state
  const [state, dispatch] = useReducer(ProductsReducer, initialState);

  //configuration
  const config = {
    headers: {
      "Content-Type": "application/json",
      Authorization: `Bearer ${localStorage.getItem("token")}`,
    },
  };

  //set the actions
  //**add product to db */
  const addProduct = async (data) => {
    const configu = {
      headers: {
        "Content-Type": "multipart/form-data",
        Accept: "application/json",
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    };
    try {
      await axios
        .post("http://localhost:8000/api/product", data, configu)
        .then((res) => {
          dispatch({
            type: ADD_PRODUCT,
            payload: res.data,
          });
        })
        .catch((err) => {
          var obj = err.response.data.errors ? err.response.data.errors : "";
          dispatch({
            type: ERRORS,
            /* payload: obj[Object.keys(obj)[0]], */
            payload: obj,
          });
        });
    } catch (error) {}
  };

  //**add product to db */
  const updateProduct = async (id, data) => {
    try {
      await axios
        .post(`http://localhost:8000/api/product/${id}`, data, config)
        .then((res) => {
          dispatch({
            type: UPDATE_PRODUCT,
            payload: res.data,
          });
        })
        .catch((err) => {
          var obj = err.response.data.errors ? err.response.data.errors : "";
          dispatch({
            type: ERRORS,
            payload: obj[Object.keys(obj)[0]],
          });
        });
    } catch (error) {}
  };

  //**add product to db */
  const deleteProduct = async (id) => {
    try {
      await axios
        .delete(`http://localhost:8000/api/product/${id}`, config)
        .then((res) => {
          dispatch({
            type: DELETE_PRODUCT,
          });
        })
        .catch((err) => {
          var obj = err.response.data.errors ? err.response.data.errors : "";
          dispatch({
            type: ERRORS,
            payload: obj[Object.keys(obj)[0]],
          });
        });
    } catch (error) {}
  };

  // get products
  const getProducts = async () => {
    try {
      const res = await axios.get("http://localhost:8000/api/products", config);
      console.log(res);
      dispatch({
        type: GET_PRODUCTS,
        payload: res.data,
      });
    } catch (error) {}
  };

  // get categories
  const getCategories = async () => {
    try {
      await axios
        .get("http://localhost:8000/api/categories", config)
        .then((res) => {
          dispatch({
            type: GET_CATEGORIES,
            payload: res.data,
          });
        })
        .error((err) => {
          console.log(err);
        });
    } catch (error) {}
  };

  //**get specific products products */
  //get products based on categories
  const getCategoryProducts = async (category) => {
    try {
      const res = await axios.get(
        `http://localhost:8000/api/products/category/${category}`
      );
      dispatch({
        type: GET_CATEGORY_PRODUCTS,
        payload: res.data[0].products,
      });
    } catch (error) {}
  };
  //get Jewelery
  const getJewelleries = async () => {
    try {
      const res = await axios.get(
        `http://localhost:8000/api/category/products/1`
      );
      dispatch({
        type: GET_JEWELLERIES,
        payload: res.data,
      });
    } catch (error) {}
  };

  //get Mens' Clothing
  const getMensClothing = async () => {
    try {
      const res = await axios.get(
        "https://fakestoreapi.com/products/category/men's clothing"
      );
      dispatch({
        type: GET_MENS_CLOTHING,
        payload: res.data,
      });
    } catch (error) {}
  };

  //get Womens' Clothing
  const getWomensClothing = async () => {
    try {
      const res = await axios.get(
        "https://fakestoreapi.com/products/category/women's clothing"
      );
      dispatch({
        type: GET_WOMENS_CLOTHING,
        payload: res.data,
      });
    } catch (error) {}
  };

  //get Electronics
  const getElectronics = async () => {
    try {
      const res = await axios.get(
        "https://fakestoreapi.com/products/category/electronics?limit=6"
      );
      dispatch({
        type: GET_ELECTRONICS,
        payload: res.data,
      });
    } catch (error) {}
  };

  //get related products
  const getRelatedProducts = async (categoryId) => {
    try {
      const res = await axios.get(
        `http://localhost:8000/api/related/products/${categoryId}`,
        config
      );

      dispatch({
        type: GET_RELATED_PRODUCTS,
        payload: res.data,
      });
    } catch (error) {}
  };

  //**get a product */
  const getProduct = async (id) => {
    try {
      const res = await axios.get(`http://localhost:8000/api/product/${id}`);
      dispatch({
        type: GET_PRODUCT,
        payload: res.data,
      });
    } catch (error) {}
  };

  // search product
  const searchProducts = (text) => {
    dispatch({
      type: SEARCH_PRODUCTS,
      payload: text,
    });
  };

  // set product to null
  const setProductToNull = () => {
    dispatch({
      type: GET_PRODUCT,
      payload: null,
    });
  };

  //Clear messages if any
  const clearMessages = () => {
    dispatch({
      type: CLEAR_MESSAGES,
    });
  };

  return (
    <ProductsContext.Provider
      value={{
        products: state.products,
        jewelleries: state.jewelleries,
        mensClothing: state.mensClothing,
        womensClothing: state.womensClothing,
        electronics: state.electronics,
        relatedProducts: state.relatedProducts,
        product: state.product,
        searchedItems: state.searchedItems,
        loading: state.loading,
        error: state.error,
        success: state.success,
        categories: state.categories,
        errors: state.errors,
        addProduct,
        updateProduct,
        deleteProduct,
        getProducts,
        getJewelleries,
        getMensClothing,
        getWomensClothing,
        getElectronics,
        getProduct,
        searchProducts,
        getCategoryProducts,
        getRelatedProducts,
        setProductToNull,
        clearMessages,
        getCategories,
      }}
    >
      {props.children}
    </ProductsContext.Provider>
  );
};

export default ProductsState;
