import React, { useState } from "react";

const Order = (props) => {
  const { item } = props;

  const [openOrder, setOpenOrder] = useState(false)


  return (
      item === null ? ('Loading') : (
      <div className="w-full block transition duration-500 min-h-16 shadow-md">
          <div className="flex justify-between">
            <img src={item.image} alt={item.price} className="w-8 h-8 rounded-full" />
            <div className="text-center">{item.title}</div>
            <i className={`text-left fa fa-angle-${openOrder ? 'down' : 'up'}`} 
            onClick={() => setOpenOrder(!openOrder)}></i>
          </div>
          <div className={`${openOrder ? 'hidden' : 'block'} flex`}>
            <div className="bg-red-500">{item.title}</div>
          </div>
      </div>
      )
  );
};

export default Order;
