import React, { useContext, useState, useEffect } from "react";
import ProductsContext from "../../context/products/ProductsContext";

const AddProduct = () => {
  const { errors, addProduct, categories, getCategories } = useContext(ProductsContext);

  //set error into state
  const [error, setError] = useState(errors);

  //set data state
  const [data, setData] = useState({
    title: "",
    price: "",
    deductions: "",
    description: "",
    qty: "",
    category: "",
    image_name: "",
  });

  //destructure data state
  const { title, price, deductions, description, qty, category, image_name } =
    data;

  //component
  useEffect(() => {
    getCategories();
    console.log(errors);
    // eslint-disable-next-line
  }, []);

  //on input change
  const onChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
  };

  // on submit
  const onSubmit = (e) => {
    e.preventDefault();
    addProduct(data)
  };

  return (
    <form onSubmit={onSubmit} className="block  md:px-4 px-8 md:ml-48 pb:8">
      <div className="w-full grid grid-cols-1 grid md:grid-cols-3 gap-4">
        <div className="block">
          <label htmlFor="Title" className="text-md text-gray-800 py-1">
            Title
          </label>
          <input
            type="text"
            name="title"
            onChange={onChange}
            value={title}
            required
            className={`${
              error?.title && "border-red-600 "
            } border border-gray-400 w-full py-1 px-1 outline-none focus:border-purple-300`}
          />
          {
            errors?.title && <label className={`hiddentext-sm italic text-red-600 py-1`}>
            {errors.title}
          </label>
          }

        </div>
        <div className="block">
          <label className="text-md text-gray-800 py-1">Price</label>
          <input
            type="text"
            name="price"
            onChange={onChange}
            value={price}
            required
            className={`${
              error?.price && "border-red-600 "
            } border border-gray-400 w-full py-1 px-1 outline-none focus:border-purple-300`}
          />
          <label className="text-sm italic text-red-600 py-1">
            Error message
          </label>
        </div>
        <div className="block">
          <label className="text-md text-gray-800">Deductions</label>
          <input
            type="text"
            name="deductions"
            onChange={onChange}
            value={deductions}
            required
            className={`${
              error?.deductions && "border-red-600 "
            } border border-gray-400 w-full py-1 px-1 outline-none focus:border-purple-300`}
          />
          <label className="text-sm italic text-red-600 py-1">
            Error message
          </label>
        </div>
        <div className="block">
          <label className="text-md text-gray-800">Description</label>
          <textarea
            type="text"
            name="description"
            onChange={onChange}
            value={description}
            required
            className={`${
              error?.description && "border-red-600 "
            } border border-gray-400 w-full py-1 px-1 outline-none focus:border-purple-300`}
          ></textarea>
          <label className="text-sm italic text-red-600 py-1">
            Error message
          </label>
        </div>
        <div className="block">
          <label className="text-md text-gray-800">Qty</label>
          <input
            type="number"
            name="qty"
            onChange={onChange}
            value={qty}
            required
            className={`${
              error?.qty && "border-red-600 "
            } border border-gray-400 w-full py-1 px-1 outline-none focus:border-purple-300`}
          />
          <label className="text-sm italic text-red-600 py-1">
            Error message
          </label>
        </div>
        <div className="block">
          <label className="text-md text-gray-800">Select Category</label>
          <select
            name="category"
            className="border border-gray-400 w-full py-1 px-1 outline-none focus:border-purple-300"
          >
            {categories?.length > 0 ? (
              categories.map((category, idx) => {
                return (
                  <option value={category.id} index={idx}>
                    {category.name}
                  </option>
                );
              })
            ) : (
              <option value="None">No category</option>
            )}
          </select>
          <label className="text-sm italic text-red-600 py-1">
            Error message
          </label>
        </div>
        <div className="block">
          <label className="text-md text-gray-800">Choose Image</label>
          <input
            type="file"
            name="image_name"
            onChange={onChange}
            value={image_name}
            required
            className={`${
              error?.title && "border-red-600 "
            } border border-gray-400 w-full py-1 px-1 outline-none focus:border-purple-300`}
          />
          <label className="text-sm italic text-red-600 py-1">
            Error message
          </label>
        </div>
      </div>
      <div className="btn text-right">
        <button
          type="submit"
          className="mb-8 py-1 mt-2 px-4 border-1 bg-purple-600
           text-white font-semibold text-xl text-center outline-none capitalize"
        >
          save product
        </button>
      </div>
    </form>
  );
};

export default AddProduct;
