import React, { useState } from "react";

const Order = (props) => {
  const { item } = props;

  const [openOrder, setOpenOrder] = useState(false)


  return (
      item === null ? ('Loading') : (
      <div className={`h-32 hover:h-48 w-full block transition duration-500 py-3 px-1 shadow-md mb-2`}>
          <div className="flex justify-between cursor-pointer" onClick={() => setOpenOrder(!openOrder)}>
            <img src={item.image} alt={item.price} className="flex-shrink-0 w-8 h-8 rounded-full mr-1" />
            <div className="flex-grow text-left bg-red-600">{item.title}</div>
            <i className={`text-center px-2 py-1 cursor-pointer text-xl text-purple-600 font-semibold fa fa-angle-${openOrder ? 'down' : 'up'}`} 
            ></i>
          </div>
          <div className={`${openOrder ? 'block' : 'block'} flex transition duartion-500`}>
            <div className="bg-red-500 p-4">
              {item.description} 
            </div>
          </div>
      </div>
      )
  );
};

export default Order;
