import React from 'react';
import Chatbot from 'react-simple-chatbot';
import CheckOrderStatus from './CheckOrderStatus';

const Chat = () => {
    return (
        <div className="mt-24 text-center w-full md:w-2/3 m-auto px-4 md:px-8">
            <Chatbot 
                steps={[
                    {
                      id: '1',
                      message: 'Please provide some basic info about your self',
                      trigger: '2',
                    },
                    {
                      id: '2',
                      message: 'What is Your name?',
                      trigger: 'entername',
                    },
                    {
                      id: 'entername',
                      user:true,
                      trigger:'enteremail'
                    },
                    {
                      id: 'enteremail',
                      message: 'Enter Your Email?',
                      trigger: '3',
                    },
                    {
                      id: '3',
                      user:true,
                      trigger:'4'
                    },
                    {
                      id: '4',
                      message: 'Good',
                      trigger:'5'
                    },
                    {
                      id: '5',
                      message: 'Please What help do you need from us?',
                      trigger:'6'
                    },
                    {
                      id:'5',
                      options:[
                        {value:1, label:'Check order status', trigger:'order_number'},
                        {value:2, label:'How to Place an Order', trigger:'how_to_place_order'},
                        {value:3, label:'Our Contacts', trigger:'our_contacts'},
                        {value:4, label:'component', trigger:'component'},
                      ],
                    },

                    /** Check about my order */
                    {
                      id: 'order_number',
                      message: 'Please Enter Order Number',
                      trigger:'order_num'
                    },
                    {
                      id: 'order_num',
                      user:true,
                      validator: (value) => {
                        if (isNaN(value)) {
                          return 'value should be a number';
                        }
                        return true;
                      },
                      trigger:'order_status'
                    },
                    {
                      id: 'order_status',
                      component: <CheckOrderStatus orderNum="{previousValue}" />,
                      trigger: '5'
                    },
                    {
                      id: '7',
                      message: 'What else?',
                      trigger: '8'
                    },
                    {
                      id: '8',
                      options:[
                        {value:1 , label:'Start Over', trigger:'5'},
                        {value:2 , label:'Check Another Order', trigger:'order_number'}
                      ],
                    },


                    /**Placing an Order */

                    {
                      id: 'how_to_place_order',
                      message: 'Lorem ipsum, dolor sit amet consectetur adipisicing elit. Maiores a modi, ullam nulla ut similique exercitationem dolor. Reprehenderit vel doloribus minima quis magnam sunt commodi similique, provident voluptate aperiam libero ducimus blanditiis laboriosam modi nulla pariatur itaque repellendus voluptates, cupiditate sit laborum porro at. Ipsum?',
                      trigger : '5'
                    },

                    /** Our contacts */

                    {
                      id: 'our_contacts',
                      message: 'Lorem ipsum, dolor sit amet consectetur adipisicing elit. Maiores a modi, ullam nulla ut similique exercitationem dolor. Reprehenderit vel doloribus minima quis magnam sunt commodi similique, provident voluptate aperiam libero ducimus blanditiis laboriosam modi nulla pariatur itaque repellendus voluptates, cupiditate sit laborum porro at. Ipsum?',
                      trigger : '5'
                    },
                    {
                      id: 'component',
                      component: <CheckOrderStatus orderNumber='1' />,
                      end:true
                    },

                  ]}
            />

        </div>
    )
}

export default Chat
