import { GET_PRODUCTS, GET_PRODUCT } from "../types";


const ProductsReducer = (state, action) => {
    switch (action.type) {
        case GET_PRODUCTS:
            return  {
                ...state,
                products: action.payload,
                loading: false
            };
        case GET_PRODUCT:
            return{
                ...state,
                product: action.payload,
            }

        default:
            return{
                ...state
            }
    }
}

export default ProductsReducer
