import {
  REGISTER,
  LOGIN,
  LOGOUT,
  ADD_TO_ORDERS,
  ERRORS,
  CLEAR_ERRORS,
  GET_ORDERS,
  GET_USER,
  CLEAR_MESSAGES,
  SUCCESS_MESSAGES,
} from "../types";

const AuthReducer = (state, action) => {
  switch (action.type) {
    case REGISTER:
    case LOGIN:
      localStorage.setItem('token', action.payload.token)
      return {
        ...state,
        user: action.payload.user,
        logedin: true,
        errors: null
      };

    case GET_USER:
      return {
        ...state,
        user: action.payload,
        logedin: true,
      };

    /* case LOGIN:
      return {
        ...state,
        user: action.payload[0].user,
        logedin: action.payload[0].token,
        errors: null
      }; */

    case LOGOUT:
      localStorage.removeItem('token')
      return {
        ...state,
        logedin: false,
        user: null,
        errors: null
      };

    case GET_ORDERS:
      return {
        ...state,
        orders: action.payload
      };

    case CLEAR_ERRORS:
      return {
        errors: null
      };


    case ERRORS:
      return {
        ...state,
        errors: action.payload
      };

    case ADD_TO_ORDERS:
      return {
        ...state,
        orders: [...state.orders, action.payload]
      };

    case SUCCESS_MESSAGES:
      return {
        ...state,
        success: action.payload,
        loading:false,
      };

    case CLEAR_MESSAGES:
      return {
        ...state,
        success:null,
        errors:null
      };

    default:
      return {
        ...state,
      };
  }
};

export default AuthReducer;
