import React from "react";
import img from "../../../images/shirt.jpg";
const Item = () => {
  return (
    <div className="block p-2 rounded border-gray-300 border bg-white">
      <div className="w-full flex">
        <div
          className="h-40 w-full bg-cover text-center overflow-hidden"
          style={{ backgroundImage: `url(${img})` }}
          title="Image"
        ></div>
        <div className="leading-normal z-10 -ml-36">
            <div className="min-w-24 bg-purple-200 text-gray-600 font-bold transition duration-500 hover:text-gray-900
            mb-2">
              view Details
            </div>
        </div>
      </div>
      <div className="px-6 pt-2 pb-2 flex justify-between">
        <span className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2 mb-2">
          Price
        </span>
        <span className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-pink-500 mr-2 mb-2">
          Add to Cart
        </span>
      </div>
    </div>
  );
};

export default Item;
