import React, { useContext, useRef, useEffect } from "react";
import ProductsContext from './context/products/ProductsContext';


const Search = () => {

  const productsContext = useContext(ProductsContext);
  const {getProducts , searchProduct } = productsContext;

  const text = useRef('')

  useEffect(() => {
    getProducts();

  }, [getProducts])

    const searchItem = (e) =>{
        e.preventDefault()
        searchProduct(text.current.value)
    }
    return (
        <form onSubmit={searchItem}>
          <input ref={text} type="text" placeholder="Search" className="pl-4 mt-4 mb-8 py-2 w-full ml-1 mr-2 rounded-full border-1"/>
        </form>
    )
}

export default Search
