import {
  REGISTER,
  LOGIN,
  LOGOUT,
  UPDATE_PASSWORD,
  ADD_TO_ORDERS
} from "../types";

const AuthReducer = (state, action, { user}) => {
  switch (action.type) {
    case REGISTER:
      return {
        ...state,
        users: [...state.users, action.payload],
        user: action.payload,
        logedin: true
      };

    case LOGIN:
      return {
        ...state,
        user: action.payload,
        logedin: true
      };

    case LOGOUT:
      return {
        ...state,
        logedin: false,
        user: null
      };

    case UPDATE_PASSWORD:
      return {
        user: state
      };

    case ADD_TO_ORDERS:
      return {
        ...state,
        orders: [...state.orders, action.payload]
      };

    default:
      return {
        ...state,
      };
  }
};

export default AuthReducer;
