import { useReducer } from "react";
import axios from 'axios';
import ProductsReducer from "./ProductsReducer";
import ProductsContext from "./ProductsContext";

import { GET_PRODUCTS, GET_PRODUCT } from "../types";

const ProductsState = (props) => {
  const initialState = {
    products: null,
    jewelleries: null,
    mensShirts: null,
    womensDresses: null,
    electronics: null,
    product: null,
    loading: false,
  };

  //initialise the state
  const [state, dispatch] = useReducer(ProductsReducer, initialState);

  //set the actions
  //**get all the products */
  const getProducts = async () => {
      try {
          const res = await axios.get('https://fakestoreapi.com/products');
            dispatch({
                type: GET_PRODUCTS,
                payload: res.data,
            });
      } catch (error) {

      }

  };

  //**get specific products products */
  //get Jewelery
  const getJewelleries = async () => {
      try {
          const res = await axios.get('https://fakestoreapi.com/products');
            dispatch({
                type: GET_PRODUCTS,
                payload: res.data,
            });
      } catch (error) {

      }

  };

  //get Mens' Shirts
  const getMensShirts = async () => {
      try {
          const res = await axios.get('https://fakestoreapi.com/products');
            dispatch({
                type: GET_PRODUCTS,
                payload: res.data,
            });
      } catch (error) {

      }

  };

  //get Womens' Shirts
  const getWomensDresses = async () => {
      try {
          const res = await axios.get('https://fakestoreapi.com/products');
            dispatch({
                type: GET_PRODUCTS,
                payload: res.data,
            });
      } catch (error) {

      }

  };

  //get Electronics
  const getElectronics = async () => {
      try {
          const res = await axios.get('https://fakestoreapi.com/products');
            dispatch({
                type: GET_PRODUCTS,
                payload: res.data,
            });
      } catch (error) {

      }

  };

  //**get a product */
  const getProduct = (id) => {
    dispatch({
      type: GET_PRODUCT,
      payload: id,
    });
  };

  return (
    <ProductsContext.Provider
      value={{
        products: state.products,
        jewelleries: state.jewelleries,
        mensShirts: state.mensShirts,
        womensDresses: state.womensDresses,
        electronics: state.electronics,
        product: state.product,
        loading: state.loading,
        getProducts,
        getJewelleries,
        getMensShirts,
        getWomensDresses,
        getElectronics,
        getProduct,
      }}
    >
        {props.children}
    </ProductsContext.Provider>
  );
};

export default ProductsState;
