import React, { useEffect, useState } from "react";
import axios from "axios";
import Chatbot from "react-simple-chatbot";

const CheckOrderStatus = (props) => {
  const [message, setMessage] = useState(null);

  useEffect(() => {
    console.log(props.orderNum);
    const checkOrder = async () => {
      await axios
        .get(
          `http://localhost:8000/api/checkorderstatus/${props.previousStep.message}`
        )
        .then((res) => {
          setMessage(res.data);
        })
        .catch((err) => {
          setMessage("Not Found");
        });
    };
    checkOrder();

    // eslint-disable-next-line
  });
  return (
    <div>
      <Chatbot
        steps={[
          {
            id: "1",
            message: "Please provide some basic info about your self",
            trigger: "2",
          },
          {
            id: "2",
            message: "What is Your name?",
            trigger: "entername",
          },
        ]}
      />
      {/* {
                message === null || message === "" || message === "Not Found" ?
                <p>Sorry your order number <span className="text-pink-600">{props.orderNum}</span> is not found</p>
                :
                    <p>Your Order status : {message.status}</p>
            } */}
    </div>
  );
};

export default CheckOrderStatus;
