import React from 'react';
import Chatbot from 'react-simple-chatbot'

const Chat = () => {
    return (
        <div className="mt-24 text-center w-full md:w-2/3 m-auto px-4 md:px-8">
            <Chatbot 
                steps={[
                    {
                      id: '1',
                      message: 'Please provide some basic info about your self',
                      trigger: '2',
                    },
                    {
                      id: '2',
                      message: 'What is Your name?',
                      trigger: 'entername',
                    },
                    {
                      id: 'entername',
                      user:true,
                      trigger:'enteremail'
                    },
                    {
                      id: 'enteremail',
                      message: 'Enter Your Email?',
                      trigger: '3',
                    },
                    {
                      id: '3',
                      user:true,
                    },
                  ]}
            />

        </div>
    )
}

export default Chat
