import React, { useEffect, useContext } from "react";
import ProductsContext from "../context/products/ProductsContext";
import CartContext from "../context/cart/CartContext";

const SingleProduct = (props) => {
  const { loading, getProduct, product } = useContext(ProductsContext);

  useEffect(() => {
    async function getSpecifiedProduct() {
      await getProduct(props.match.params.id);
    }

    getSpecifiedProduct();
  });
  return (
    <div>
      {loading !== null ? (
        product !== null ? (
          <div>
            <h2 className="title">{product.title}</h2>
            <div className="div">
              <img src={product.image} alt="" />
            </div>
            <div className="flex">
              <span className="">Price: {product.price}</span>
              {cart?.length > 0 &&
              cart.filter((item) => item.id === product.id).length > 0 ? (
                <span
                  className="inline-block bg-gray-200 rounded-full px-3 py-1 text-xs font-semibold text-pink-500 mr-2 mb-2 cursor-pointer"
                  onClick={removeProductFromCart}
                >
                  Remove From Cart
                </span>
              ) : (
                <span
                  className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-pink-500 mr-2 mb-2 cursor-pointer"
                  onClick={addProductToCart}
                >
                  Add to Cart
                </span>
              )}
            </div>
            <div>
              <p className="">{product.description}</p>
            </div>
          </div>
        ) : (
          "Product Not Found"
        )
      ) : (
        "Loading"
      )}
    </div>
  );
};

export default SingleProduct;
