import React from "react";

const Product = (props) => {
  const { product } = props



  return props.product === null ? (
    "Loading"
  ) : (
    <tr className="border-8 border-l-4 border-r-4 border-white">
      <td className="hidden pt-4 pb-4 md:table-cell">
        <div>
          <img
            src={`http://localhost:8000/storage/images/products/${product.image}`}
            className="w-20 rounded"
            alt="Thumbnail"
          />
        </div>
      </td>
      <td className="pt-4 pb-4">{product.title}</td>
      <td className="pt-4 pb-4 text-center">{product.price}</td>
      <td className="hidden pt-4 pb-4 md:table-cell text-center">{product.deduction}</td>
      <td className="pt-4 pb-4 text-center ">{product.qty}</td>
      <td className="flex pt-4 pb-4 md:text-right">
        <i className="fa fa-edit text-md font-semibold text-blue-600 px-2"></i>
        <i className="fa fa-trash text-md font-semibold text-red-600 px-2"></i>
      </td>
    </tr>
  );
};

export default Product;
